mapper.insert:

insert into order_info (o_id, o_type, o_contacts,
o_start_date, o_end_date, o_price,
o_shipping_address, o_receiving_address, s_id,
d_id, o_state, o_remarks
)
values (#{oId,jdbcType=VARCHAR}, #{oType,jdbcType=INTEGER}, #{oContacts,jdbcType=VARCHAR},
#{oStartDate,jdbcType=TIMESTAMP}, #{oEndDate,jdbcType=TIMESTAMP}, #{oPrice,jdbcType=DECIMAL},
#{oShippingAddress,jdbcType=VARCHAR}, #{oReceivingAddress,jdbcType=VARCHAR}, #{sId,jdbcType=INTEGER},
#{dId,jdbcType=INTEGER}, #{oState,jdbcType=VARCHAR}, #{oRemarks,jdbcType=VARCHAR}
)

mapper.update:
update order_info
set o_state = #{oState}

    , d_id = #{dId}


    , o_end_date = now()

where o_id = #{oId,jdbcType=VARCHAR}

mapper.select:
select * from order_info


        o_state like concat('%',#{oState},'%')


        and o_contacts like concat('%',#{oContacts},'%')


        and o_id like concat('%',#{oId},'%')


        and s_Id = #{sId}


        and d_Id = #{dId}

select count(*) from order_info


        o_state like concat('%',#{oState},'%')


        and o_contacts like concat('%',#{oContacts},'%')


        and o_id like concat('%',#{oId},'%')


        and s_Id = #{sId}


        and d_Id = #{dId}

select count(*) from order_info where o_state like concat('%', #{oType}, '%')

    and s_Id = #{sId}


    and d_id = #{dId}
